package basic;

public class demoloops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		
		for  (int i=0;i<10;i++) {
			
			
			System.out.println("Hello World");
		}

		//first we need a counter
		
		
		
		int c=0;
		int sum=0;
		while (c<=20) {
			sum=sum+c;
			c++;
		}
		
		System.out.println(sum);
		
		//dowhile loop
		//foreach loop
		
	}

}
